import styles from '../Styles/Cart.module.css'
import Modal from '../UI/Modal';
import { useContext } from 'react';
import CartContext from '../../Store/Cart-context';

const Cart = (props) => {

    const cartContext = useContext(CartContext)

    //получаем сумму 
    const totalAmount = `$${cartContext.totalAmount.toFixed(2)}`
    //если существует боль
    const hasitems = cartContext.items.length > 0

    const cartItems = (<ul className={styles['cart-items']}>{cartContext.items.map((item) => (
        <li>{item.name}</li>
    ))}</ul>)

    return (
        // чтобы при клике на backDrop закрыть модальное окно также нужно этому блоку передать данную функцию 
        // поэтому функцию передаем в компонент Modal где находится наш backDrop
        <Modal onHideCart = {props.onHideCart}>
            {cartItems}
            <div className={styles.total}>
                <span>Итого</span>
                <span>{totalAmount}</span>
            </div>
            <div className={styles.actions}>
                {/* Вешаем на кнопку обработчик события  */}
            <button onClick = {props.onHideCart} className={styles['button--alt']}>Закрыть</button>
            <button  className={styles.button}>Заказать</button>
            </div>
        </Modal>
    )
}

export default Cart;
