import React, { useState, useEffect } from 'react';



function MovableBlock() {
  const [position, setPosition] = useState({ top: 0, left: 0 });

  useEffect(() => {
    const handleKeyPress = (event) => {
      switch (event.key) {
        case 'ArrowUp':
          setPosition({ top: position.top - 10, left: position.left });
          break;
        case 'ArrowDown':
          setPosition({ top: position.top + 10, left: position.left });
          break;
        case 'ArrowLeft':
          setPosition({ top: position.top, left: position.left - 10 });
          break;
        case 'ArrowRight':
          setPosition({ top: position.top, left: position.left + 10 });
          break;
        default:
          break;
      }
    };

    window.addEventListener('keydown', handleKeyPress);

    return () => {
      window.removeEventListener('keydown', handleKeyPress);
    };
  }, [position]);

  const blockStyle = {
    width: '100px',
    height: '100px',
    backgroundColor: 'blue',
    position: 'absolute',
    top: position.top + 'px',
    left: position.left + 'px',
    transform: 'scale(1.5)'
  };

  return <div style={blockStyle}></div>;
}

export default MovableBlock;
