import React, { useState, useEffect } from 'react';


function MovableBlock() {
  const [position, setPosition] = useState({ top: 0, left: 0 });

  useEffect(() => {
    const handleKeyPress = (event) => {
      const newPosition = { top: position.top, left: position.left };

      switch (event.key) {
        case 'ArrowUp':
          newPosition.top -= 10;
          break;
        case 'ArrowDown':
          newPosition.top += 10;
          break;
        case 'ArrowLeft':
          newPosition.left -= 10;
          break;
        case 'ArrowRight':
          newPosition.left += 10;
          break;
        default:
          break;
      }

      // Проверяем, не пересекается ли новая позиция с другими элементами
      if (!isColliding(newPosition)) {
        setPosition(newPosition);
      }
    };

    const isColliding = (newPosition) => {
      const blockElement = document.getElementById('movableBlock');
      const otherElement = document.getElementById('otherElement'); // Другой элемент, с которым проверяем столкновение

      const blockRect = blockElement.getBoundingClientRect();
      const otherRect = otherElement.getBoundingClientRect();
      console.log('blockRect:', blockRect);
      console.log('otherRect:', otherRect);


      return (
        newPosition.left < otherRect.right &&
        newPosition.right > otherRect.left &&
        newPosition.top < otherRect.bottom &&
        newPosition.bottom > otherRect.top
      );
    };

    
    window.addEventListener('keydown', handleKeyPress);

    return () => {
      window.removeEventListener('keydown', handleKeyPress);
    };
  }, [position]);

  const blockStyle = {
    width: '100px',
    height: '100px',
    backgroundColor: 'blue',
    position: 'absolute',
    top: position.top + 'px',
    left: position.left + 'px',
    transition: 'top 0.2s, left 0.2s',
    boxShadow: '4px 4px 6px gray',
  };

  const wall = {
    backgroundColor:'brown',
    width:'50px',
    height:'100vh'
  }
  return (
    <div>
        <div id="movableBlock" style={blockStyle}></div>
        <div id = 'otherElement' style = {wall}></div>
    </div>
  );
}

export default MovableBlock;
